void Alumno(int x, int y) {

	int BotonPlay[8][4] = { 
{10,1,1,10},
{10,1,15,10},
{10,15,15,10},
{12,12,12,12},
{15,12,12,15},
{15,5,5,15},
{10,5,5,10},
{9,5,5,9} };




	cursor(x, y);
	for (int i = 0; i < 8; ++i) {

		for (int j = 0; j < 4; ++j) {

			colorBackground(BotonPlay[i][j]);
			cout << " ";
		}
		cout << endl;
		y++;
		cursor(x, y);
	}
	colorBackground(1);

}
